function terminationFunc(_this){
	$(_this).addClass("hide");
	$(_this).parent("._operation").find("button").eq(1).removeClass("hide");
	return ;
}
/*----index2-add----*/
var threshold_num = 1;
function thresholdAddFunc(){
	var threshold_add = $("#threshold_add");
	var threshold_div = $("#threshold_div_1");
	threshold_num++;
	var new_div = $("<div></div>");
	new_div.attr("id","threshold_div_"+threshold_num);
	new_div.html(threshold_div.html());
	new_div.find("label").eq(0).text("门槛"+threshold_num+"：");
	new_div.find("label").eq(1).text("提成"+threshold_num+"：");
	new_div.find("label").eq(0).attr("for","threshold_"+threshold_num);
	new_div.find("label").eq(1).attr("for","commission_"+threshold_num);
	new_div.find("input").eq(0).attr("id","threshold_"+threshold_num);
	new_div.find("input").eq(1).attr("id","commission_"+threshold_num);
	new_div.find("button").eq(0).attr("onclick","thresholdDelFunc('threshold_div_"+threshold_num+"')");
	new_div.find("button").eq(0).removeClass("hide");
	threshold_div.after(new_div);
	return ;
}
function thresholdDelFunc(id){
	var threshold_div = $("#"+id);
	threshold_div.remove();
	return ;
}